    $adjust-lightness,
    $adjust-saturation
  );

  @include colorize-el-background( $base-color );
  @include colorize-el-border( $base-color );
  text-shadow: $pixel $pixel 0 $text-shadow;
}

composer-node.style:
$toolbar-children-color: $primary-color-darker;
$toolbar-empty-color: hsl( 210, 10%, 90% );
$children-color: $list-panel-color;

.composer-node > .header-bar .header-bar__title::before,
.composer-node__children > .header-bar .header-bar__title::before {
  content: "▴";
  padding-right: spacing( 0.5 );
}

.composer-node--collapsed > .header-bar .header-bar__title::before,
.composer-node__children--collapsed > .header-bar .header-bar__title::before {
  content: "▾";
}

.composer-node:not( [data-depth="0"] ) > .header-bar {
  cursor: move;
}

.composer-node__view {
  margin-bottom: spacing( 0.5 );
  overflow: hidden;
  padding: spacing( 0.5 );

  color: $near-white-color;
  @include colorize-el-background( $near-black-color );
}

.composer-node__view:last-child {
  margin-bottom: 0;
}

.composer-node__main {
  padding: spacing( 0.75 );
  margin: spacing( 0.25 );
  margin-top: 0;

  @include colorize-el-background( $panel-color );
  box-shadow: $medium-shadow;
}

.composer-node--collapsed > .composer-node__main,
.composer-node__children--collapsed > .composer-node__main {
  height: 0;
  margin: 0;
  padding: 0;
  border: 0;
  overflow: hidden;
}

.composer-node__children {
  overflow: hidden;

  @include colorize-el-background( $children-color );
  @include colorize-el-border-inset( $panel-color );
  box-shadow: inset $medium-shadow;
}

.composer-node__children > ul {
  margin: 0;
  padding: spacing( 0.5 );
  list-style: none;
}

.composer-node__children--collapsed > ul {
  height: 0;
  padding: 0;
  overflow: hidden;
}

.composer-node__children > ul > li {
  margin: 0;
  padding: 0;
}

.composer-node > .header-bar {
  color: $near-white-color;
  @include colorize-el( $secondary-color );
}

.composer-node--empty > .header-bar {
  color: $near-black-color;
  @include colorize-el( $toolbar-empty-color );
}

.children-bar {
  color: $near-white-color;
  @include colorize-el( $toolbar-children-color );
}

document.style:
html {
  box-sizing: border-box;
  font-family: sans-serif;
}

*, *:before, *:after {
  box-sizing: inherit;
  margin: 0;
  padding: 0;
}

body {
  background: #fff;
  color: #000;
  line-height: $line-height;
  overflow-y: scroll;
}

.gu-unselectable {
  list-style: none;
}

.gu-mirror {
  position: fixed !important;
  margin: 0 !important;
  z-index: 9999 !important;
  opacity: 0.8;
  -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=80)";
  filter: alpha(opacity=80);
}

.gu-hide {
  display: none !important;
}

.gu-unselectable {
  -webkit-user-select: none !important;
  -moz-user-select: none !important;
  -ms-user-select: none !important;
  user-select: none !important;
}

.gu-transit {
  opacity: 0.2;
  -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=20)";
  filter: alpha(opacity=20);
}

$progress-width: spacing( 16 );
$progress-height: spacing( 4 );
$progress-padding: spacing( 1 );

$progress-color: $panel-color;
$progress-bar-color: $success-color;

.progress {
  position: fixed;
  top: 50%;
  left: 50%;
  width: $progress-width;
  height: $progress-height;
  margin-left: $progress-width / -2;
  margin-top: $progress-height / -2;
  padding: $progress-padding;

  @include colorize-el( $progress-color );
  box-shadow: $large-shadow;
}

.progress > progress {
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;

  width: $progress-width - $progress-padding * 2;
  height: $progress-height - $progress-padding * 2;

  border: none;
  background: #fff;
  color: $progress-bar-color;
  box-shadow: inset $tiny-shadow;
}

.progress > progress::-webkit-progress-bar {
  border: none;
  background: #fff;
  box-shadow: inset $tiny-shadow;
}

.progress > progress::-webkit-progress-value {
  @include colorize-el( $progress-bar-color );
}

.progress > progress::-moz-progress-bar {
  @include colorize-el( $progress-bar-color );
}

.progress--hidden {
  display: none;
}

.modal {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba( 255, 255, 255, 0.5 );
}

.modal--hidden {
  display: none;
}

.overlay-grid {
  position: relative;
}

.overlay-grid:active:after {
  display: none;
}

.overlay-grid:after {
  background:
    linear-gradient( to right, rgba( 200, 225, 255, 0.6 ) $pixel, transparent $pixel ),
    linear-gradient( to bottom, rgba( 200, 225, 255, 0.6 ) $pixel, transparent $pixel );
  background-size: $base-spacing $base-spacing;
  bottom: 0;
  margin-top: 0;
  content: '';
  display: block;
  height: auto;
  left: 0;
  pointer-events: none;
  position: absolute;
  right: 0;
  top: 0;
  width: auto;
  z-index: 9999;
}

header-bar.style:
.header-bar {
  color: $near-black-color;
  @include colorize-el( $toolbar-color );
  padding: spacing( 0.25, $pixel );
  text-shadow: none;
  box-shadow: $medium-shadow;

  display: flex;
  flex-direction: row;
  align-items: center;
}

.header-bar__title {
  padding: 0 spacing( 0.25 );
  flex-grow: 1;
}

.header-bar__actions {
  display: flex;
  flex-direction: row;
}

.header-bar__actions > li {
  display: block;
}

.header-bar__actions .button {
  display: block;
}

header-window.style:
.header-window {
  @include colorize-el( $toolbar-color );
  padding: spacing( 0.25, $pixel );
  box-shadow: $medium-shadow;
}

.header-window > .header-bar {
  background: none;
  border: none;
  box-shadow: none;
  padding: 0;
  padding-bottom: spacing( 0.25 );
}

.header-window__main {
  padding: spacing( 0.75, $pixel );

  @include colorize-el-background( $panel-color );
  @include colorize-el-border-inset( $toolbar-color );
  box-shadow: inset $small-shadow;
}

input.style:
.input {
  display: block;
  color: $near-black-color;
  @include colorize-el( $panel-color );
  text-shadow: none;
  padding: spacing( 0.5, $pixel );
  box-shadow: $small-shadow;
}

.input__title {
  font-weight: bold;
}

.input__title small {
  font-style: italic;
}

input-fieldset.style:
.input-fieldset {
  @include colorize-el( $toolbar-color );
  border: 0;
  text-shadow: none;
  box-shadow: $medium-shadow;
  padding: 0;
}

.input-fieldset .input-fieldset {
  box-shadow: $small-shadow;
}

.input-fieldset > legend {
  color: $near-black-color;
  @include colorize-el( $toolbar-color );
  font-weight: bold;
  width: 100%;
  text-shadow: none;
  padding: spacing( 0.5, $pixel );
}

.input-fieldset > div {
  @include colorize-el( $panel-color );
  @include colorize-el-border-inset( $toolbar-color );
  padding: spacing( 0.75, $pixel );
  margin: spacing( 0.25 );
  margin-top: 0;
  box-shadow: inset $small-shadow;
}

.input-fieldset--list {
  @include colorize-el( $list-color );
  border: 0;
}

.input-fieldset--list > legend {
  @include colorize-el( $list-color );
  text-shadow: none;
}

.input-fieldset--list > div {
  @include colorize-el( $list-panel-color );
  @include colorize-el-border-inset( $list-color );
}

.input-fieldset--select {
  @include colorize-el( $select-color );
  border: 0;
}

.input-fieldset--select > legend {
  @include colorize-el( $select-color );
  text-shadow: none;
}

.input-fieldset--select > div {
  @include colorize-el( $select-inset-color );
  @include colorize-el-border-inset( $select-color );
}

input-number.style:
.input-number__title {
  margin-bottom: spacing( 0.5 );
}

.input-number input {
  display: block;
  width: 100%;
  font-size: 1rem;
  padding: spacing( 0.25, $pixel );
  @include colorize-el-background( $input-color );
  @include colorize-el-border-inset( $panel-color );
  box-shadow: inset $tiny-shadow;
}

input-selectable-icon.style:
.input-selectable-icon {
  display: block;
  padding: spacing( 0.25, $pixel );
  width: spacing( 6 );
  height: spacing( 6 );
  text-align: center;
  overflow: hidden;

  color: $near-black-color;
  @include colorize-el( $input-color );
}

.input-selectable-icon:not( .input-selectable-icon--selected ):hover {
  @include colorize-el( $input-color-hover );
}

.input-selectable-icon--selected {
  color: $near-white-color;
  @include colorize-el( $highlight-color );
}

.input-selectable-icon i {
  margin: spacing( 0.5 );
  margin-bottom: 0;
}

.input-selectable-icon__title {
  padding: spacing( 0.25 );
  height: spacing( 1 );
  font-size: spacing( 0.75 );
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.input-selectable-icon__title input, .input-selectable-icon__title span {
  display: inline;
}

input-text.style:
.input-text__title {
  margin-bottom: spacing( 0.5 );
}

.input-text input {
  display: block;
  width: 100%;
  font-size: 1rem;
  padding: spacing( 0.25, $pixel );
  @include colorize-el-background( $input-color );
  @include colorize-el-border-inset( $panel-color );
  box-shadow: inset $tiny-shadow;
}

row.style:
.row {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
}

theme.style:
// whitespace

$line-height: 1.25;
$base-spacing: 1.25rem;
$pixel: $base-spacing * 0.05;

@function spacing( $multiplier:1, $subtract:0 ){
  @return ( $base-spacing * $multiplier ) - $subtract;
}

// palette
$primary-color: hsl(210, 85%, 60% );
$primary-color-darker: hsl(210, 85%, 40% );
$secondary-color: hsl( 210, 15%, 40% );
$success-color: hsl( 150, 85%, 40% );
$warning-color: hsl( 40, 100%, 50% );
$alert-color: hsl( 0, 85%, 50% );
$near-white-color: hsl( 210, 5%, 95% );
$light-grey-color: hsl( 210, 5%, 80% );
$near-black-color: hsl( 210, 85%, 15% );

$primary-grey-color: hsl( 210, 25%, 80% );
$success-grey-color: hsl( 150, 25%, 80% );
$success-white-color: hsl( 150, 25%, 95% );

// derived colors (from palette)
$button-color: $primary-color;
$button-color-hover: advanced-lighten( $button-color );
$button-color-active: advanced-darken( $button-color );
$button-alert-color: $alert-color;
$button-alert-color-hover: advanced-lighten( $button-alert-color );
$button-alert-color-active: advanced-darken( $button-alert-color );

$panel-color: $near-white-color;

$list-color: $primary-grey-color;
$list-panel-color: hsl( 210, 95%, 95% );

$input-color: advanced-lighten( $near-white-color );
$input-color-hover: advanced-darken( $near-white-color );

$select-color: $success-grey-color;
$select-inset-color: hsl( 150, 75%, 95% );
$select-panel-color: $success-white-color;

$toolbar-color: $light-grey-color;

$highlight-color: $primary-color;

// shadows
$large-shadow: spacing( 0.25 ) spacing( 0.25 ) spacing( 1 ) rgba( 0, 0, 0, 0.25 );
$medium-shadow: spacing( 0.125 ) spacing( 0.125 ) spacing( 0.5 ) rgba( 0, 0, 0, 0.25 );
$small-shadow: spacing( 0.0625 ) spacing( 0.0625 ) spacing( 0.25 ) rgba( 0, 0, 0, 0.25 );
$tiny-shadow: spacing( 0.03125 ) spacing( 0.03125) spacing( 0.125 ) rgba( 0, 0, 0, 0.25 );

.theme-primary {
  @include colorize-el( $primary-color );
  color: $near-white-color;
}

.padding-half {
  padding: spacing( 0.5 );
}

.margin-bottom-1 {
  margin-bottom: spacing( 1 );
}

.margin-vertical-1 {
  margin: spacing( 1 ) 0;
}

.shadow-medium {
  box-shadow: $medium-shadow;
}
